package com.example.YuCeClient.util;import android.content.Context;import android.text.TextUtils;import android.view.Gravity;import android.widget.Toast;import com.example.YuCeClient.background.HCApplicaton;public class ToastUtil extends Toast {    private static Toast mToast;    private ToastUtil(Context context) {        super(context);    }    public static Toast makeText(Context context, CharSequence text, int duration) {        if (mToast == null) {            if (context == null) {                mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", duration);            } else {                mToast = Toast.makeText(context, "", duration);            }        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(text);        mToast.setDuration(duration);        return mToast;    }    public static Toast makeText(Context context, int resId, int duration) {        if (mToast == null) {            if (context == null) {                mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", duration);            } else {                mToast = Toast.makeText(context, "", duration);            }        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(context.getString(resId));        mToast.setDuration(duration);        return mToast;    }    public static void makeShortText(CharSequence text) {        if (mToast == null) {            mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(text);        mToast.setDuration(Toast.LENGTH_SHORT);        mToast.show();    }    public static void makeShortTextCenter(CharSequence text) {        if (mToast == null) {            mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(text);        mToast.setDuration(Toast.LENGTH_SHORT);        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.show();    }    public static void makeShortText(int resId) {        if (mToast == null) {            mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(HCApplicaton.getInstance().getApplicationContext().getString(resId));        mToast.setDuration(Toast.LENGTH_SHORT);        mToast.show();    }    public static void makeLongText(CharSequence text) {        if (mToast == null) {            mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", Toast.LENGTH_LONG);        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(text);        mToast.setDuration(Toast.LENGTH_LONG);        mToast.show();    }    public static void makeLongText(int resId) {        if (mToast == null) {            mToast = Toast.makeText(HCApplicaton.getInstance().getApplicationContext(), "", Toast.LENGTH_LONG);        }        mToast.setGravity(Gravity.CENTER, 0, 0);        mToast.setText(HCApplicaton.getInstance().getApplicationContext().getString(resId));        mToast.setDuration(Toast.LENGTH_LONG);        mToast.show();    }    public static void showErrorMessage(Request.RequestException e, String title) {        String message;        if (e.getResponse() == null) {            if (e.getStatusCode() != Request.RequestException.INVALID_CODE && !TextUtils.isEmpty(e.getResponse())) {                message = "网络繁忙";            } else {                message = "网络繁忙";            }        } else {            message = e.getResponse();        }        if (title.length() == 0) {            title = "请求失败";        }        if (TextUtils.isEmpty(message)) {            makeShortText(title);        } else {            makeShortText(title + "," + message);        }    }}